import sysimport numpy.linalgdef findNearestNeighbor(train_projection, image):    min_distance = sys.float_info.max    min_index = 0    for i in range(train_projection.shape[0]):        distance = calculateDistance(train_projection[i], image)        if distance < min_distance:            min_distance, min_index = distance, i    return min_indexdef calculateDistance(curr, temp):    return numpy.linalg.norm(curr - temp)    